mapper.select:
select 

from internal_annex

  enable = 0

    AND id = #{id,jdbcType=VARCHAR}


   AND upload_time = #{uploadTime,jdbcType=TIMESTAMP}


    AND  uploader = #{uploader,jdbcType=VARCHAR}


    AND  modifer = #{modifer,jdbcType=VARCHAR}


    AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    AND  node = #{node,jdbcType=CHAR}


    AND file_url = #{fileUrl,jdbcType=VARCHAR}


    AND file_module = #{fileModule,jdbcType=VARCHAR}


    AND file_type = #{fileType,jdbcType=VARCHAR}


    AND file_name = #{fileName,jdbcType=VARCHAR}


    AND  file_size = #{fileSize,jdbcType=DOUBLE}


    AND  back_up1 = #{backUp1,jdbcType=VARCHAR}


    AND  back_up2 = #{backUp2,jdbcType=VARCHAR}


    AND  back_up3 = #{backUp3,jdbcType=VARCHAR}


    AND  back_up4 = #{backUp4,jdbcType=VARCHAR}


    AND   back_up5 = #{backUp5,jdbcType=VARCHAR}


    AND  back_up6 = #{backUp6,jdbcType=VARCHAR}


    AND  back_up7 = #{backUp7,jdbcType=VARCHAR}


    AND  back_up8 = #{backUp8,jdbcType=VARCHAR}


    AND  back_up9 = #{backUp9,jdbcType=VARCHAR}


    AND back_up10 = #{backUp10,jdbcType=VARCHAR}
