mapper.select:
select 

,

from internal_construction

     enable = 0

      AND id = #{id,jdbcType=VARCHAR}


      AND project_id = #{projectId,jdbcType=VARCHAR}


      AND  dev_unit = #{devUnit,jdbcType=VARCHAR}


      AND  plan_start_time = #{planStartTime,jdbcType=TIMESTAMP}


      AND plan_end_time = #{planEndTime,jdbcType=TIMESTAMP}


      AND  actual_start_time = #{actualStartTime,jdbcType=TIMESTAMP}


      AND actual_end_time = #{actualEndTime,jdbcType=TIMESTAMP}


      AND test_pass_time = #{testPassTime,jdbcType=TIMESTAMP}
