ipr.status as iprStatus,ipr.enable as iprEnable,ip.*,ipr.*
from internal_project ip LEFT JOIN internal_persons_resource ipr ON ip.id = ipr.project_id where ip.id = #{id,jdbcType=VARCHAR}
ORDER BY  ipCreateTime  ASC  limit #{pageNo},#{pageSize}

