mapper.select:
select

from internal_todo


    AND id = #{id,jdbcType=VARCHAR}


    AND currentstep_id = #{currentstepId,jdbcType=VARCHAR}


    AND step_desc=#{stepDesc,jdbcType=VARCHAR}


    AND relateddomain=#{relateddomain,jdbcType=VARCHAR}


    AND relateddomain_id=#{relateddomainId,jdbcType=VARCHAR}


    AND sender_id=#{senderId,jdbcType=VARCHAR}


    AND sender_time=#{senderTime,jdbcType=TIMESTAMP}


    AND receiver_id=#{receiverId,jdbcType=VARCHAR}


    AND todo_type=#{todoType,jdbcType=VARCHAR}


    AND actiontime=#{actiontime,jdbcType=TIMESTAMP}


    AND deletetime=#{deletetime,jdbcType=TIMESTAMP}


    AND  status=#{status,jdbcType=VARCHAR}


    AND backup_2=#{backup2,jdbcType=VARCHAR}


    AND backup_3=#{backup3,jdbcType=VARCHAR}
