mapper.insert:
insert into t_xietong_agenda (ID, begin_time, end_time,
  meeting_subject, meeting_place, remark,
  isdelete, createtime, am_pm,
  ispublish, createrid, type
  )
values (#{id,jdbcType=VARCHAR}, #{beginTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP},
  #{meetingSubject,jdbcType=VARCHAR}, #{meetingPlace,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR},
  #{isdelete,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{amPm,jdbcType=INTEGER},
  #{ispublish,jdbcType=INTEGER}, #{createrid,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}
  )

mapper.select:
select *
from t_xietong_agenda a

  id = #{id}
  and a.begin_time >= #{beginTime} and a.begin_time <=#{endTime}
  and a.end_time >= #{beginTime} and a.end_time <=#{endTime}

ORDER BY a.begin_time

select count(*) from t_xietong_agenda where id in (select t.agenda_id from t_xietong_agenda_human t where t.begin_time >=#{beginTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/ and t.begin_time <=#{endTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/
and t.participant_value = #{remark}
and t.isdelete=1) and isdelete=1
