mapper.delete:
delete from t_xietong_agenda_release where id in(select distinct t.agenda_id from t_xietong_agenda_human t where t.isdelete=1
and t.participant_id = #{remark}
and t.begin_time >=#{beginTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/
and t.begin_time <=#{endTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/
)

mapper.select:
select * from t_xietong_agenda_release where id in (select distinct t.agenda_id from t_xietong_agenda_human t where t.isdelete=1
and t.participant_id = #{remark}
and t.begin_time >=#{beginTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/
and t.begin_time <=#{endTime}/*STR_TO_DATE(,"%Y-%m-%d %H:%i:%s")*/
) and isdelete=1 ORDER BY begin_time
