mapper.update:
update t_xietong_mr_reservation


    mr_id = #{mrId,jdbcType=VARCHAR},


    begintime = #{begintime,jdbcType=TIMESTAMP},


    endtime = #{endtime,jdbcType=TIMESTAMP},


    res_id = #{resId,jdbcType=VARCHAR},


    res_name = #{resName,jdbcType=VARCHAR},


    res_dep_id = #{resDepId,jdbcType=VARCHAR},


    res_dep_name = #{resDepName,jdbcType=VARCHAR},


    isdelete = #{isdelete,jdbcType=INTEGER},


    createtime = #{createtime,jdbcType=TIMESTAMP},


    meeting_subject = #{meetingSubject,jdbcType=VARCHAR},


    compere = #{compere,jdbcType=VARCHAR},


    participants = #{participants,jdbcType=VARCHAR},


    status = #{status,jdbcType=INTEGER},


  meeting_id=#{meetingId,jdbcType=VARCHAR}


  meeting_name=#{meetingName,jdbcType=VARCHAR}


  meeting_content=#{meetingContent,jdbcType=VARCHAR}


  filepath=#{filepath,jdbcType=VARCHAR}


  meeting_supplies=#{meetingSupplies,jdbcType=VARCHAR}


  meeting_status=#{meetingStatus,jdbcType=VARCHAR}


  issue=#{issue,jdbcType=INTEGER}


where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select * from t_xietong_mr_reservation where res_id=#{res_id} and isdelete!=0

 begintime>=#{beginTime}


 endtime<=#{endTime}

 order by status, begintime desc

select * from t_xietong_mr_reservation where res_id=#{res_id} and isdelete!=0 and status=2

 begintime>=#{beginTime}


 endtime<=#{endTime}


 meeting_subject like '%${meeting_subject}%'

 order by begintime desc
