mapper.select:
select  count(*) from t_xietong_supply_record


    supply_name=#{supplyName}


    and  receiptor_name=#{receiptorName}

  select * from t_xietong_supply_record
where supply_id=#{supplyId} and supply_type=#{supplyType}
order by createtime desc

mapper.insert:
insert into t_xietong_supply_record (ID, supply_id, supply_name,
supply_type, receiptor_id, receiptor_depart_id,
reamark, isdelete, createtime,
amount, receiptor_name, receiptor_depart_name,price
)
values (#{id,jdbcType=VARCHAR}, #{supplyId,jdbcType=VARCHAR}, #{supplyName,jdbcType=VARCHAR},
#{supplyType,jdbcType=VARCHAR}, #{receiptorId,jdbcType=VARCHAR}, #{receiptorDepartId,jdbcType=VARCHAR},
#{reamark,jdbcType=VARCHAR}, #{isdelete,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP},
#{amount}, #{receiptorName,jdbcType=VARCHAR}, #{receiptorDepartName,jdbcType=VARCHAR}, #{price,jdbcType=TIMESTAMP}
)
