mapper.select:
SELECT u.*,d.`leader_id` AS departmentLeadId,d.`name` AS departmentName,r.`id` AS rid , r.`name` AS roleName ,r.`isdelete` AS rstatus,d.`isdelete` AS dstatus FROM t_xietong_user u LEFT JOIN t_xietong_department d ON u.`departmentId` = d.`id` LEFT JOIN t_xietong_user_role ur ON ur.`user_id` = u.`id` LEFT JOIN t_xietong_role r ON ur.`role_id` = r.`id` WHERE u.`isdelete` = "1" AND ur.`id` IN (SELECT MAX(ur.`id`) FROM t_xietong_user_role r GROUP BY ur.`id`)
AND ( u.`name_cn` LIKE CONCAT('%',#{name},'%') OR u.`ext3` LIKE CONCAT('%',#{name},'%'))
AND u.`departmentId` = #{departmentId}
ORDER BY d.`sort` , u.`sort` limit #{pageNo},#{pageSize}

SELECT COUNT(u.`id`) FROM t_xietong_user u LEFT JOIN t_xietong_department d ON u.`departmentId` = d.`id` LEFT JOIN t_xietong_user_role ur ON ur.`user_id` = u.`id` LEFT JOIN t_xietong_role r ON ur.`role_id` = r.`id` WHERE u.`isdelete` = "1" AND ur.`id` IN (SELECT MAX(ur.`id`) FROM t_xietong_user_role r GROUP BY ur.`id`)
AND u.`name_cn` LIKE CONCAT('%',#{name}, '%')
AND u.`departmentId` = #{departmentId}

select count(u.id)from t_xietong_user u where u.isdelete=1

          AND  ( u.`name_cn` LIKE CONCAT('%',#{name},'%') OR u.`ext3` LIKE CONCAT('%',#{name},'%'))



           AND u.`departmentId` = #{departmentId}

select * from t_xietong_user u where u.isdelete=1

          AND  ( u.`name_cn` LIKE CONCAT('%',#{name},'%') OR u.`ext3` LIKE CONCAT('%',#{name},'%'))



           AND u.`departmentId` = #{departmentId}

       ORDER BY  u.`sort` limit #{pageNo},#{pageSize}
