mapper.select:
select p.project_code as projectCode,p.project_name as projectName,a.dept as purchaseDept, a.supplier as supplier,a.amount as amount,a.tender_num as tenderNum from tender_application a,project p where a.project_id=p.id
and p.project_name like "%"#{projectName,jdbcType=VARCHAR}"%"
and p.project_code like "%"#{projectCode,jdbcType=VARCHAR}"%"
and a.method=#{method,jdbcType=VARCHAR}
ORDER BY a.create_time LIMIT #{rowNum,jdbcType=INTEGER},#{pageCount,jdbcType=INTEGER}

select p.project_code as projectCode, p.project_name as projectName, r.purchase_dept as dept, r.apply_date as applyDate, r.amount as amount, r.id AS requestId, r.request_num AS tenderNum, r.project_id AS projectId, r.method AS method from purchase_request r,project p where r.status="10" AND r.project_id=p.id
and p.project_name like concat("%", #{projectName},"%")
and p.project_code like concat("%", #{projectCode},"%")
and r.method=#{method,jdbcType=VARCHAR}
ORDER BY r.apply_date desc
limit #{params.pageNo},#{params.pageSize}
